import React, { useEffect, useState } from "react";
import { apiFetch } from "../utils.js";

export default function UserList({ onEdit, onDelete }) {
  const [notes, setNotes] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    async function fetchNotes() {
      try {
        const data = await apiFetch("/api/notes"); // sesuaikan endpoint
        setNotes(data);
      } catch (err) {
        setError("Gagal memuat data catatan.");
      }
    }
    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Yakin ingin menghapus catatan ini?")) return;
    try {
      await apiFetch(`/api/notes/${id}`, { method: "DELETE" });
      setNotes((prev) => prev.filter((note) => note.id !== id));
    } catch {
      alert("Gagal menghapus catatan.");
    }
  };

  if (error)
    return (
      <p
        style={{
          color: "#b00020",
          fontWeight: "600",
          textAlign: "center",
          marginTop: 20,
        }}
      >
        {error}
      </p>
    );

  return (
    <div
      style={{
        maxWidth: 480,
        margin: "40px auto",
        padding: 30,
        backgroundColor: "#ffffff",
        borderRadius: 12,
        boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: "#1a237e",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: 25 }}>Daftar Catatan</h2>
      {notes.length === 0 ? (
        <p
          style={{
            color: "#4a7023",
            fontWeight: "500",
            textAlign: "center",
            marginTop: 20,
          }}
        >
          Tidak ada catatan.
        </p>
      ) : (
        <ul style={{ listStyleType: "none", padding: 0, margin: 0 }}>
          {notes.map((note) => (
            <li
              key={note.id}
              style={{
                backgroundColor: "#f9f9f9",
                marginBottom: 15,
                padding: 20,
                borderRadius: 8,
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                position: "relative",
              }}
            >
              <h3
                style={{
                  margin: "0 0 8px 0",
                  color: "#1565c0",
                  fontWeight: "600",
                }}
              >
                {note.title}
              </h3>
              <small
                style={{
                  color: "#1565c0",
                  fontWeight: "500",
                }}
              >
                {new Date(note.date).toLocaleString()}
              </small>
              <p style={{ marginTop: 12, color: "#444" }}>{note.content}</p>

              <div
                style={{
                  position: "absolute",
                  top: 20,
                  right: 20,
                  display: "flex",
                  gap: 10,
                }}
              >
                <button
                  style={{
                    backgroundColor: "#1976d2",
                    color: "white",
                    border: "none",
                    borderRadius: 6,
                    padding: "6px 12px",
                    cursor: "pointer",
                    fontWeight: "600",
                    fontSize: 14,
                    boxShadow: "0 2px 6px rgba(25, 118, 210, 0.4)",
                    transition: "background-color 0.3s ease",
                  }}
                  onClick={() => onEdit(note.id)}
                  onMouseEnter={(e) => (e.target.style.backgroundColor = "#115293")}
                  onMouseLeave={(e) => (e.target.style.backgroundColor = "#1976d2")}
                >
                  Edit
                </button>
                <button
                  style={{
                    backgroundColor: "#d32f2f",
                    color: "white",
                    border: "none",
                    borderRadius: 6,
                    padding: "6px 12px",
                    cursor: "pointer",
                    fontWeight: "600",
                    fontSize: 14,
                    boxShadow: "0 2px 6px rgba(211, 47, 47, 0.5)",
                    transition: "background-color 0.3s ease",
                  }}
                  onClick={() => handleDelete(note.id)}
                  onMouseEnter={(e) => (e.target.style.backgroundColor = "#b71c1c")}
                  onMouseLeave={(e) => (e.target.style.backgroundColor = "#d32f2f")}
                >
                  Hapus
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
