import React, { useState } from "react";

export default function LoginForm({ onLoginSuccess }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const res = await fetch(`${process.env.REACT_APP_API_BASE_URL || "https://notes-backend197-174534490336.us-central1.run.app"}/api/auth/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem("token", data.token);
        onLoginSuccess();
      } else {
        setError(data.error || "Login gagal");
      }
    } catch {
      setError("Gagal menghubungi server");
    }
  };

  return (
    <div style={styles.wrapper}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2 style={styles.title}>Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Masuk</button>
        {error && <p style={styles.error}>{error}</p>}
      </form>
    </div>
  );
}

const styles = {
  wrapper: {
    backgroundColor: "#e6eeff", // biru muda yang memenuhi seluruh layar
    minHeight: "100vh",
    margin: 0,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "20px",
  },
  form: {
    backgroundColor: "white",
    padding: "40px 50px",
    borderRadius: "12px",
    boxShadow: "0 6px 20px rgba(0,0,0,0.12)",
    width: "400px",  // lebarkan card
    boxSizing: "border-box",
  },
  title: {
    color: "#002366",
    marginBottom: "30px",
    textAlign: "center",
    fontWeight: "700",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    fontSize: "24px",
  },
  input: {
    width: "100%",
    padding: "14px 18px",
    marginBottom: "25px",
    borderRadius: "8px",
    border: "2px solid #002366",
    fontSize: "16px",
    fontFamily: "inherit",
    outline: "none",
    transition: "border-color 0.3s",
  },
  button: {
    width: "100%",
    padding: "14px 18px",
    backgroundColor: "#cc0000",
    border: "none",
    borderRadius: "8px",
    color: "white",
    fontWeight: "700",
    fontSize: "18px",
    cursor: "pointer",
    fontFamily: "inherit",
    transition: "background-color 0.3s",
  },
  error: {
    marginTop: "15px",
    color: "#cc0000",
    fontWeight: "600",
    textAlign: "center",
  },
};
